pub fn spritesAttrSlot(attr_name: []const u8) ?usize {
    if (std.mem.eql(u8, attr_name, "position")) {
        return 0;
    }
    if (std.mem.eql(u8, attr_name, "texcoord")) {
        return 1;
    }
    if (std.mem.eql(u8, attr_name, "sprite_pos")) {
        return 2;
    }
    if (std.mem.eql(u8, attr_name, "sprite_scale")) {
        return 3;
    }
    return null;
}
pub fn spritesImageSlot(img_name: []const u8) ?usize {
