    if (std.mem.eql(u8, ub_name, "fs_params")) {
        return 1;
    }
    return null;
}
pub fn spritesUniformblockSize(ub_name: []const u8) ?usize {
    if (std.mem.eql(u8, ub_name, "vs_params")) {
        return @sizeOf(VsParams);
    }
    if (std.mem.eql(u8, ub_name, "fs_params")) {
        return @sizeOf(FsParams);
    }
    return null;
}
pub fn spritesUniformOffset(ub_name: []const u8, u_name: []const u8) ?usize {
    if (std.mem.eql(u8, ub_name, "vs_params")) {
        if (std.mem.eql(u8, u_name, "projection")) {
            return 0;
        }
    }
    if (std.mem.eql(u8, ub_name, "fs_params")) {
        if (std.mem.eql(u8, u_name, "sprite_rect")) {
            return 0;
        }
    }
    return null;
}
pub fn spritesUniformDesc(ub_name: []const u8, u_name: []const u8) ?sg.GlslShaderUniformDesc {
    if (std.mem.eql(u8, ub_name, "vs_params")) {
        if (std.mem.eql(u8, u_name, "projection")) {
            var desc: sg.ShaderUniformDesc = .{};
            desc.type = .MAT4;
